import React, { useRef, useEffect, useState } from "react";
import { select, geoPath, geoAlbersUsa, min, max, scaleLinear } from "d3";
import useResizeObserver from "./useResizeObserver";
import data from "./states.json"


/**
 * Component that renders a map of Germany.
 */
console.log(data.features(property))

function GeoChart({ data, property }) {
  const svgRef = useRef();
  const wrapperRef = useRef();
  const dimensions = useResizeObserver(wrapperRef);
  const [selectedState, setSelectedState] = useState(null);

  // will be called initially and on every data change
  useEffect(() => {
    const svg = select(svgRef.current);

    const minProp = min(data.features, feature => feature.properties[property]);
    const maxProp = max(data.features, feature => feature.properties[property]);
    const colorScale = scaleLinear()
      .domain([minProp, maxProp])
      .range(["#ccc", "red"]);
      console.log(minProp, maxProp)
    // use resized dimensions
    // but fall back to getBoundingClientRect, if no dimensions yet.
    const { width, height } =
      dimensions || wrapperRef.current.getBoundingClientRect();

    // projects geo-coordinates on a 2D plane
    const projection = geoAlbersUsa()
      .fitSize([width, height], selectedState || data)
      .precision(100);

    // takes geojson data,
    // transforms that into the d attribute of a path element
    const pathGenerator = geoPath().projection(projection);

    // render each country
    svg
      .selectAll(".state")
      .data(data.features)
      .join("path")
      .attr('d', pathGenerator)
      .attr("class", "state")
      .style('cursor', 'pointer')
      .attr('fill', d => {return (d.STATE === "26") ? '#CC442F' : '#6eabcc'})
      .attr('opacity', d => {return (d.STATE === "26") ? 1 : .7})
      .on('click', d => {
          svg.selectAll('.state')
          .attr('fill', '#6eabcc')
          .attr('opacity', .7)});

      // .transition()
      // .attr("fill", feature => colorScale(feature.properties[property]))
      // .attr("d", feature => pathGenerator(feature));

    // render text
    svg
      .selectAll(".label")
      .data([selectedState])
      .join("text")
      .attr("class", "label")
      .attr("x", 10)
      .attr("y", 25);
  }, [data, dimensions, property, selectedState]);

  return (
    <div ref={wrapperRef} style={{ marginBottom: "2rem" }}>
      <svg ref={svgRef}></svg>
    </div>
  );
}

export default GeoChart;
