'use strict';

module.exports = function validate(region) {
  var isValid = true;

  if (region.right < region.left) {
    isValid = false;
    region.right = region.left;
  }

  if (region.bottom < region.top) {
    isValid = false;
    region.bottom = region.top;
  }

  return isValid;
};
